import { Component, OnInit } from '@angular/core';
import { Aluno } from './models/aluno.model';
import { AlunosService } from './services/alunos.service';

@Component({
  selector: 'app-alunos',
  templateUrl: './alunos.component.html',
  styleUrls: ['./alunos.component.css']
})
export class AlunosComponent implements OnInit {
  alunos: Aluno[] = [];
  novoAluno: Aluno = {
    nome: '',
    email: '',
    sexo: '',
    dataNascimento: ''
  };

  constructor(private alunosService: AlunosService) { }

  ngOnInit(): void {
    let teste = this.alunosService.getAlunos();
    console.log(teste)
    this.alunos = teste
  }

  adicionarAluno(): void {
    console.log(this.novoAluno)
    this.alunosService.addAluno(this.novoAluno);
    this.novoAluno = {
      nome: '',
      email: '',
      sexo: '',
      dataNascimento: ''
    };
  }

  atualizarAluno(aluno: Aluno): void {
    this.alunosService.updateAluno(aluno);
  }

  excluirAluno(email: string): void {
    this.alunosService.deleteAluno(email);
  }
}
<div>
  <h2>Alunos</h2>

  <!-- Lista de alunos -->
  <ul>
    <li *ngFor="let aluno of alunos">
      {{ aluno.nome }} - {{ aluno.email }}
      <button (click)="excluirAluno(aluno.email)">Excluir</button>
    </li>
  </ul>

  <!-- Formulário para adicionar novo aluno -->
  <h3>Novo Aluno</h3>
  <form (submit)="adicionarAluno()">
    <label>
      Nome:
      <input type="text" [(ngModel)]="novoAluno.nome" name="nome" required>
    </label>
    <br>
    <label>
      Email:
      <input type="email" [(ngModel)]="novoAluno.email" name="email" required>
    </label>
    <br>
    <label>
      Sexo:
      <select [(ngModel)]="novoAluno.sexo" name="sexo" required>
        <option value="M">Masculino</option>
        <option value="F">Feminino</option>
      </select>
    </label>
    <br>
    <label>
      Data de Nascimento:
      <input type="date" [(ngModel)]="novoAluno.dataNascimento" name="dataNascimento" required>
    </label>
    <br>
    <button type="submit">Adicionar</button>
  </form>
</div>
